	@SuppressWarnings("finally")
	public boolean loginCOE(String cemail, String cpsw, HttpServletRequest request) throws SQLException {
		Boolean authorized = false;
		String encode = new String();
		HttpSession session = request.getSession(true);
		try {
			connection = DriverManager.getConnection(dbUrl, "root", "");

			PreparedStatement ps = (PreparedStatement) connection
					.prepareStatement("SELECT * FROM coes where userID=? and password=?");
			ps.setString(1, cemail);
			ps.setString(2, cpsw);
			ResultSet rs = ps.executeQuery();
			authorized = rs.next();
			if (authorized) {
				session.setAttribute("instuID", rs.getString(1));
				PreparedStatement ps2 = (PreparedStatement) connection
						.prepareStatement("SELECT * FROM instu_details where instuID=?");
				ps2.setString(1, rs.getString(1));
				ResultSet rs2 = ps2.executeQuery();
				if (rs2.next()) {
					session.setAttribute("instuName", rs2.getString(2));
					session.setAttribute("instuAddr", rs2.getString(6));
					session.setAttribute("instuZip", rs2.getInt(7));
					byte[] imgData1 = rs.getBytes(7); // blob field
					encode = Base64.getEncoder().encodeToString(imgData1);

					if (encode.isEmpty()) {
						encode = "";
					}
				}
			}

		} catch (Exception e) {
			encode = "";
			e.printStackTrace();
		} finally {
			session.setAttribute("pic", encode);
			return authorized;

		}

	}

	public boolean updateCOE(InputStream pic, String uid) throws SQLException {

		try {

			connection = DriverManager.getConnection(dbUrl, "root", "");
			PreparedStatement updateStatement = (PreparedStatement) connection
					.prepareStatement("UPDATE coes SET picture=? WHERE userID=?");
			;

			updateStatement.setBlob(1, pic);
			updateStatement.setString(2, uid);

			int updatedRecords = updateStatement.executeUpdate();
			if (updatedRecords > 0) {
				return true;
			} else {
				return false;
			}

		} catch (Exception e) {
			e.printStackTrace();
			return false;
		}

	}

