	public boolean institutionRegister(String iname, String addr, String country, String state, String dist, int zip,
			String site, String cemail, String cpsw, String secq, String seca) throws SQLException {
		int insertcoe = 0;
		try {

			connection = DriverManager.getConnection(dbUrl, "root", "");
			String insertInstuQuery = "insert into instu_details (InstuName,address,country,state,district,zip,website) values(?,?,?,?,?,?,?)";
			PreparedStatement insertStatement = (PreparedStatement) connection.prepareStatement(insertInstuQuery);

			insertStatement.setString(1, iname);
			insertStatement.setString(2, addr);
			insertStatement.setString(3, country);
			insertStatement.setString(4, state);
			insertStatement.setString(5, dist);
			insertStatement.setInt(6, zip);
			insertStatement.setString(7, site);

			int insertInstu = insertStatement.executeUpdate();
			if (insertInstu == 1) {
				Statement st = (Statement) connection.createStatement();
				String getIDQuerry = "select MAX(InstuID) from instu_details";
				ResultSet rs = st.executeQuery(getIDQuerry);
				if (rs.next()) {
					mx = rs.getInt(1);
				}

				String insertCOEQuery = "insert into coes (InstuID,userID,password,secQ,secA,utype) values(?,?,?,?,?,?)";
				PreparedStatement insertStatement2 = (PreparedStatement) connection.prepareStatement(insertCOEQuery);

				insertStatement2.setInt(1, mx);
				insertStatement2.setString(2, cemail);
				insertStatement2.setString(3, cpsw);
				insertStatement2.setString(4, secq);
				insertStatement2.setString(5, seca);
				insertStatement2.setString(6, "COE");

				insertcoe = insertStatement2.executeUpdate();

			}
			return true;

		} catch (Exception e) {
			e.printStackTrace();

			Statement st = (Statement) connection.createStatement();
			String deleteID = "DELETE from instu_details where InstuID=" + mx;
			if (insertcoe != 0) {
				int rs = st.executeUpdate(deleteID);

			}

			return false;
		}

	}

