	public boolean recoverPassword(String email, String utype, int newpass) throws SQLException {
		Boolean done = false;
		String query = new String();
		SendMail recoverPassword = new SendMail();

		try {
			connection = DriverManager.getConnection(dbUrl, "root", "");

			if (utype.equals("COE")) {
				query = "UPDATE coes SET password=? WHERE userID=?";
			} else if (utype.equals("Student")) {
				query = "UPDATE students SET password=? WHERE emailID=?";
			} else {
				if (utype.equals("Teacher")) {
					query = "UPDATE teachers SET password=? WHERE teacherID=?";
				}
			}
			PreparedStatement ps = (PreparedStatement) connection.prepareStatement(query);
			ps.setInt(1, newpass);
			ps.setString(2, email);

			int counts = ps.executeUpdate();
			if (counts > 0) {
				done = true;
				recoverPassword.sendPassword(email, newpass);
				return done;

			}
		} catch (Exception e) {
			e.printStackTrace();
			return done;
		}
		return done;

	}

